import React, { useState } from 'react';
import { ReactReader } from 'react-reader';

const LeituraEPUB = () => {
    const epubUrl = 'https://magical-marzipan-1029e1.netlify.app/Original - Andrei Barbuto.epub';
    const [currentPage, setCurrentPage] = useState(0);
    const [inputPage, setInputPage] = useState(0);
    const [totalPages, setTotalPages] = useState(0);
    const [flag, setFlag] = useState(true);

    const handlePage = () => {
        if (inputPage >= 1 && inputPage <= totalPages) {
            setCurrentPage(inputPage - 1); // As páginas EPUB geralmente têm índice 0
        } else {
            console.log('Número de página inválido');
        }
    };

    const handleLocation = (location) => {
        if (location !== 'epubcfi(/6/4!/4/2/1:0)') {
            console.log(location);
            setFlag(false);
        }
    };

    const handleTocChange = (toc) => {
        // Supondo que o toc do EPUB forneça informações sobre o número total de páginas
        setTotalPages(toc.length);
    };

    return (
        <div className='containerLeitura'>
            <input
                type='number'
                min='1'
                max={totalPages}
                onChange={(e) => setInputPage(parseInt(e.target.value, 10))}
            />
            <button onClick={handlePage}>Ir para a Página</button>

            {flag ? (
                <ReactReader
                    url={epubUrl}
                    title="Tribos Ancestrais"
                    locationChanged={(location) => handleLocation(location)}
                    tocChanged={(toc) => handleTocChange(toc)}
                    showToc={true}
                />
            ) : (
                <ReactReader
                    url={epubUrl}
                    title="Tribos Ancestrais"
                    locationChanged={(location) => handleLocation(location)}
                    location={`epubcfi(${currentPage})`}
                    tocChanged={(toc) => handleTocChange(toc)}
                    showToc={true}
                />
            )}
        </div>
    );
};

export default LeituraEPUB;
