export const loadPost = async () => {

    const postResponse = fetch('https://jsonplaceholder.typicode.com/posts').then((response) => response.json());
    const postImg = fetch('https://jsonplaceholder.typicode.com/photos').then((response) => response.json());
    const [posts, imgs] = await Promise.all([postResponse, postImg])
    const postsJson = await posts.json();
    const imgsJson = await imgs.json();
    const postsImgs = postsJson.map((post, index) => {
        return { ...post, cover: imgsJson[index].url }
    })

    return postsImgs

}
