import React, { useState } from 'react';
import { ReactReader } from 'react-reader';

const LeituraEPUB = () => {
    // Substitua com o caminho real para o seu arquivo EPUB
    const epubUrl = 'https://magical-marzipan-1029e1.netlify.app/alice.epub';

    const [showToc, setShowToc] = useState(false);
    const [chapters, setChapters] = useState([]);

    const handleToggleToc = () => {
        setShowToc(!showToc);
    };

    const handleChapterClick = (chapter) => {
        // Ao clicar em um capítulo, você pode fazer o que quiser, como ir para o capítulo
        console.log('Clicou no capítulo:', chapter);
        // Exemplo: ir para o capítulo usando a propriedade location do ReactReader
        // Note que o índice do capítulo pode ser diferente do índice da página
        // Então, você precisa ajustar conforme necessário.
        // Supondo que `chapter` tenha uma propriedade como `startCfi`:
        // setar a location para o índice do capítulo clicado
        // setChapters(prevChapters => prevChapters.map(ch => ({ ...ch, active: ch === chapter })));
    };

    const handleTocChanged = (newChapters) => {
        setChapters(newChapters);
    };

    return (
        <div className='containerLeitura'>
            <h1>teste</h1>

            <button onClick={handleToggleToc}>Toggle TOC</button>

            {showToc && (
                <div>
                    <h2>Table of Contents</h2>
                    <ul>
                        {chapters.map((chapter, index) => (
                            <li key={index} onClick={() => handleChapterClick(chapter)}>
                                {chapter.label}
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            <ReactReader
                url={epubUrl}
                title="Tribos Ancestrais"
                locationChanged={(epubcifi) => console.log(epubcifi)}
                epubOptions={{ width: '100%', height: '100%' }}
                location={12}
                swipeable={true}
                showToc={showToc}
                tocChanged={handleTocChanged}
            />

            <h1>teste</h1>
        </div>
    );
};

export default LeituraEPUB;
