import logo from './logo.svg';
import './App.css';
import { Post } from './components/Post';
import { useState } from 'react';
import { loadPost } from './utils/loadPosts';

function App() {
  const [posts, setPosts] = useState([]);
  const load = async () => {
    await setPosts(loadPost())
  }




  return (
    <div onClick={load}>
      <h1>
        Cards informativos
      </h1>
      <div>
        {posts.map(post => {
          <Post post={post} />
        })}
      </div>
    </div>
  );
}

export default App;
