import React, { useState } from 'react';
import { ReactReader,Reader } from 'react-reader';
import PdfViewer from '../PdfViewer';

const Leitura = () => {
    const [location, setLocation] = useState(0);
    const pdfUrl = '../book/ori/alice.epub'

    return (
        <div className="containerLeitura">
            <div className="backLeitura">
            <Reader
          url='../boo'
          title="Book Title"
          epubOptions={{
            restore: true,
            storage: true,
            spreads: true,
          }}
          getRendition={(rendition) => {
            // Trigger the onEpubReady callback with the epub object
            onEpubReady(rendition.book);
          }}
        />
            </div>
        </div>
    );
};



export default Leitura;
