import React, { useState } from 'react';
import { ReactReader } from 'react-reader';

const LeituraEPUB = () => {
    const epubUrl = 'https://magical-marzipan-1029e1.netlify.app/alice.epub';
    const [currentPage, setCurrentpage] = useState(0);
    const [inputPage, setInputPage] = useState(0);
    const [TotcurrentLocation, setTotCurrentLocation] = useState();
    const [toc, setToc] = useState([]); // Adicionando estado para armazenar o sumário

    const handlePage = () => {
        if (inputPage < toc.length) {
            setCurrentpage(inputPage);
        } else {
            console.log('Capítulo não encontrado.');
        }
    };

    const handleLocation = (location) => {
        setTotCurrentLocation(location.totalPages);
    };

    const handleTocChange = (newToc) => {
        setToc(newToc);
    };

    return (
        <div className='containerLeitura'>
            <input onChange={(e) => setInputPage(e.target.value)}></input>
            <button onClick={handlePage}>Ir</button>

            <ReactReader
                url={epubUrl}
                title="Tribos Ancestrais"
                locationChanged={(location) => handleLocation(location)}
                location={currentPage}
                showToc={true}
                onTocChange={handleTocChange} // Manipula a mudança no sumário
            />
        </div>
    );
};

export default LeituraEPUB;
