import './App.css';
import Post from './components/Post';
import './components/style.css'
import { loadPost } from './utils/loadPosts';
import { useEffect, useState } from 'react';
import "./components/style.css"

function App() {
  const [postsImgs, setPostsImgs] = useState([]).

  useEffect(() => {
    const load = async () => {
      const loadData = await loadPost();
      setPostsImgs(loadData)
    }
    load();
  }, [])
  let numeroPost = 6

  const maisPosts = () => {
    numeroPost += 6
    console.log(numeroPost);

  }




  return (
    <div style={{ backgroundColor: "lightgray" }} >
      <h1 style={{ textAlign: "center", marginBottom: "2vw" }}>
        Cards informativos
      </h1>
      <div className="quadrado">
        {postsImgs.slice(0, numeroPost).map(post => (
          <Post key={post.id} post={post} />
        ))}
      </div>
      <button style={{ width: "5vw", height: "2vw", fontSize: "2vw", alignItems: "center",marginLeft:"50vw" }}

        onClick={maisPosts}
      >+</button>
    </div>
  );
}

export default App;
