import React, { useState, useEffect } from 'react';
import { ReactReader, ReactReaderStyle } from 'react-reader';
import menuLeitura from '../img/menuLeitura.png';

// ... restante do código

const LeituraEPUB = () => {
    // ... restante do código

    return (
        <div className="containerLeitura">
            <div className='menuLeitura'>
                <img src={menuLeitura} style={{ zIndex: changeZIndex }} />
            </div>

            {flag ? (
                <ReactReader
                    url={epubUrl}
                    title="Tribos Ancestrais"
                    locationChanged={(location) => handleLocation(location)}
                    showToc={true}
                />
            ) : (
                <ReactReader
                    url={epubUrl}
                    title="Tribos Ancestrais"
                    locationChanged={(location) => handleLocation(location)}
                    location={TotcurrentLocation}
                    readerStyles={{
                        ...ReactReaderStyle,  // Use os estilos padrão
                        arrow: {
                            color: 'white',
                        },
                        arrowHover: {
                            color: '#ccc',
                        },
                        readerArea: {
                            backgroundColor: '#000',
                            transition: undefined,
                        },
                        titleArea: {
                            color: '#ccc',
                        },
                        tocArea: {
                            background: '#111',
                        },
                        tocButtonExpanded: {
                            background: '#222',
                        },
                        tocButtonBar: {
                            background: '#fff',
                        },
                        tocButton: {
                            color: 'white',
                        },
                        tocItem: {
                            color: '#ccc',
                        },
                    }}
                    showToc={true}
                    tocChanged={(e) => console.log(e)}
                />
            )}
        </div>
    );
};

export default LeituraEPUB;
