import React, { useState, useEffect } from 'react';
import EPUBJS from 'epubjs';

const Leitura = () => {
  const [book, setBook] = useState(null);
  const [location, setLocation] = useState(0);

  useEffect(() => {
    const initBook = async () => {
      const epubUrl = '../caminho/para/seu/livro.epub'; // Substitua pelo caminho real do seu livro EPUB
      const newBook = await EPUBJS(epubUrl);
      setBook(newBook);
    };

    initBook();
  }, []);

  const handleNextPage = () => {
    if (book) {
      setLocation(book.locations.next(location));
    }
  };

  const handlePrevPage = () => {
    if (book) {
      setLocation(book.locations.prev(location));
    }
  };

  return (
    <div className="containerLeitura">
      <div className="backLeitura">
        {book && (
          <div>
            <div>
              <button onClick={handlePrevPage}>Página Anterior</button>
              <button onClick={handleNextPage}>Próxima Página</button>
            </div>
            <div>
              <p>{book.locations.locationFromCfi(location).displayed}</p>
              <div dangerouslySetInnerHTML={{ __html: book.spine.get(location).contents }} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Leitura;


