import React, { useState, useRef, useEffect } from 'react';
import { ReactReader, ReactReaderStyle } from 'react-reader';

const LeituraEPUB = () => {
  const [page, setPage] = useState('');
  const rendition = useRef(undefined);
  const toc = useRef([]);
  const [location, setLocation] = useState('');

  useEffect(() => {
    const savedLocation = localStorage.getItem('epubLocation');
    if (savedLocation) {
      setLocation(savedLocation);
    }
  }, []);

  const handleLocationChanged = (loc) => {
    setLocation(loc);
    if (rendition.current && toc.current) {
      const { displayed, href } = rendition.current.location.start;
      const chapter = toc.current.find((item) => item.href === href);
      setPage(
        `Page ${displayed.page} of ${displayed.total} in chapter ${
          chapter ? chapter.label : 'n/a'
        }`
      );
    }
  };

  const handleLocation = (location) => {
    if (location !== 'epubcfi(/6/4!/4/2/1:0)') {
      console.log(location);
      setLocation(location.toString());
      localStorage.setItem('epubLocation', location.toString());
    }
  };

  return (
    <div className="containerLeitura">
      <div className='menuLeitura'>
        <img src={menuLeitura} style={{ zIndex: changeZIndex }} />
      </div>

      {flag ? (
        <ReactReader
          url={epubUrl}
          locationChanged={(location) => handleLocationChanged(location)}
          showToc={true}
          getRendition={(rend) => (rendition.current = rend)}
          tocChanged={(_toc) => (toc.current = _toc)}
        />
      ) : (
        <ReactReader
          url={epubUrl}
          locationChanged={(location) => handleLocationChanged(location)}
          location={location}
          readerStyles={{
            ...ReactReaderStyle,
            container: {
              backgroundColor: '#F4E2C7',
              ...ReactReaderStyle.container,
            },
            tocArea: {
              ...ReactReaderStyle.tocArea,
              marginTop: '5vw',
              height: '150vw',
              color: '#522828',
            },
            // Restante dos estilos aqui
          }}
          showToc={true}
          tocChanged={(e) => console.log(e)}
        />
      )}
    </div>
  );
};

export default LeituraEPUB;
